export default {
  name: 'index',
  data() {
    return {
      listLoading: true,
      list: [],
      pageSize: 10,
      total: 0,
      currentPage: 1,
      assetsDialogVisible: false,
      assets: {}
    }
  },
  created() {
    this.getAll()
  },
  methods: {
    getAll() {
      this.listLoading = true
      this.$http.get('/assets-vulns/all', {
        page: this.currentPage - 1,
        size: this.pageSize
      }).then(response => {
        this.list = response.content
        this.total = response.totalElements
        this.listLoading = false
      })
    },
    coderHandleClick(row) {
      this.assets = { ...row }  //将选中的该行对象赋值给浮窗
      this.assetsDialogVisible = true
    },
    misreportHandleClick(row) {
      this.$http.delete(`/assets-vulns/${row.id}`).catch(v => {
        this.getAll()
        this.$message({
          type: 'success',
          message: '删除成功!'
        })
      })
    },
    updateSubmit() {
      this.$http.post('/assets-vulns/assets', {
        ...this.assets
      }).then(v => {
        this.getAll()
        this.$message({
          type: 'success',
          message: '保存成功!'
        })
        this.assetsDialogVisible = false
      })
    }
  }
}
