import echarts from 'echarts/lib/echarts'
import jsonData from './data'
import 'echarts-gl'

const lineColor = ['green', 'red', 'blue', 'yellow']
export default {
  name: 'Index',
  data() {
    return {}
  },

   <!--配置地球和线的样子开始-->
  mounted() {
    const myChart = echarts.init(window.document.getElementById('main'))
    const routes = jsonData.routes.map(airline => {
      return {
        coords: [this.getAirportCoord(airline[1]), this.getAirportCoord(airline[2])],
        // value: 10,
        lineStyle: {
          color: `${lineColor[Math.floor(Math.random() * 4)]}`
        }
      }
    })
    myChart.setOption({
      backgroundColor: '#000',
      globe: {
        // baseTexture: 'https://www.echartsjs.com/examples/data-gl/asset/world.topo.bathy.200401.jpg',
        baseTexture: require('../../assets/globe/base-texture.jpg'),
        heightTexture: require('../../assets/globe/height-texture.jpg'),

        // heightTexture: 'https://www.echartsjs.com/examples/data-gl/asset/bathymetry_bw_composite_4k.jpg',
        shading: 'lambert',
        // shading: 'realistic',
        postEffect: {
          enable: true
        },
        globeRadius: 80,
        globeOuterRadius: 120,
        // environment:require('../../assets/globe/environment.jpg'),
        light: {
          ambient: {
            intensity: 0.4
          },
          main: {
            intensity: 0.4
          }
        },
        viewControl: {
          autoRotate: true,
          animationDurationUpdate: 2000,
          targetCoord: ''
        }
      },
      series: {
        type: 'lines3D',
        coordinateSystem: 'globe',
        // blendMode: 'lighter',
        blendMode: 'source-over',
        effect: {
          show: true,
          period: 4,
          trailWidth: 1,
          trailLength: 0.3,
          trailOpacity: 0.3
          // constantSpeed: 8
          // trailColor: 'rgb(29,56,150)'
        },
        lineStyle: {
          width: 1.5,
          color: 'rgb(29,56,150)',
          opacity: 0.02
        },
        data: routes
      }
    })
  },
  <!--配置地球和线的样子结束-->
  <!--加载路径数据开始-->
  methods: {
    getAirportCoord(idx) {
      return [jsonData.airports[idx][3], jsonData.airports[idx][4]]
    },
